import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
  ComponentProps
} from "streamlit-component-lib"
import {
  SearchEmbed,
  AuthType,
  init,
  prefetch,
  EmbedEvent,
  HostEvent
}
from '@thoughtspot/visual-embed-sdk';
import React, {useEffect, ReactElement, ReactNode} from "react"


interface State {
  numClicks: number
  isFocused: boolean
}

/**
 * This is a React-based component template. The `render()` function is called
 * automatically when your component should be re-rendered.
 */


 function ThoughtspotComponent({args}: ComponentProps): ReactElement {
  Streamlit.setFrameHeight(600)

  let host = args["host"];
  let sso = args["sso"];
  let height = args["height"]
  let width = args["width"]

  init({
    thoughtSpotHost: host,
    authType: sso ? AuthType.SAML : AuthType.None,
  });

  useEffect(()=>{
      var element : any =  document.getElementById('ts-embed');
      const searchEmbed = new SearchEmbed(element, {
        frameParams: {
            width: width,
            height: height,
        },
      });
      searchEmbed.render()
    })
    return (
      <div style={{width:width,height:height}} id="ts-embed">
        TS Embed
      </div>  
    )
 }

// "withStreamlitConnection" is a wrapper function. It bootstraps the
// connection between your component and the Streamlit app, and handles
// passing arguments from Python -> Component.
//
// You don't need to edit withStreamlitConnection (but you're welcome to!).
export default withStreamlitConnection(ThoughtspotComponent)
