import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import {
  SearchEmbed,
  AuthType,
  init,
  prefetch,
  EmbedEvent,
  HostEvent
}
from '@thoughtspot/visual-embed-sdk';

import React, { ReactNode, useEffect } from "react"

interface State {
  numClicks: number
  isFocused: boolean
}

/**
 * This is a React-based component template. The `render()` function is called
 * automatically when your component should be re-rendered.
 */
class MyComponent extends StreamlitComponentBase<State> {
  public state = { numClicks: 0, isFocused: false }

  public render = (): ReactNode => {

    const name = this.props.args["name"]
    function isloaded(){
      console.log("loaded!!")
    }
    setTimeout(function(){
      var element : any =  document.getElementById('ts-embedz');
      element.innerHTML = "it changed"
      const searchEmbed = new SearchEmbed(element, {
        frameParams: {
            width: '100%',
            height: '100%',
        },
      });
      searchEmbed.on(EmbedEvent.Init, isloaded)
      element.innerHTML = "it changed2"
    },1000)


    const { theme } = this.props

    return (
      <div style={{height:'600px',width:'600px'}} id="ts-embed" >
          TS EMBED!!
      </div>
    )
  }


}

// "withStreamlitConnection" is a wrapper function. It bootstraps the
// connection between your component and the Streamlit app, and handles
// passing arguments from Python -> Component.
//
// You don't need to edit withStreamlitConnection (but you're welcome to!).
export default withStreamlitConnection(MyComponent)
